/* Fonts loaded asynchronously - see index.html */
@import url('https://fonts.googleapis.com/css2?family=Roboto:wght@100;300;400;500;700&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(260, 30%, 8%);
  --foreground: hsl(0, 0%, 95%);
  --muted: hsl(260, 20%, 12%);
  --muted-foreground: hsl(240, 5%, 65%);
  --popover: hsl(260, 25%, 10%);
  --popover-foreground: hsl(0, 0%, 95%);
  --card: hsl(260, 20%, 10%);
  --card-foreground: hsl(0, 0%, 95%);
  --border: hsl(260, 15%, 20%);
  --input: hsl(260, 15%, 15%);
  --primary: hsl(16, 100%, 73%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(180, 100%, 50%);
  --secondary-foreground: hsl(0, 0%, 100%);
  --accent: hsl(180, 50%, 65%);
  --accent-foreground: hsl(0, 0%, 100%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --ring: hsl(16, 100%, 73%);
  --radius: 0.5rem;

  /* Custom colors */
  --salmon: hsl(16, 100%, 73%);
  --salmon-muted: hsl(16, 80%, 65%);
  --salmon-dark: hsl(16, 60%, 35%);
  --cyan: hsl(180, 100%, 50%);
  --cyan-muted: hsl(180, 80%, 60%);
  --cyan-dark: hsl(180, 40%, 15%);
  --cyan-bright: hsl(180, 100%, 65%);
  --purple-dark: hsl(260, 40%, 12%);
  --grey-dark: hsl(220, 15%, 10%);
}

/* Admin panel gradient card class */
.admin-gradient-card {
  background: linear-gradient(135deg, 
    hsl(320, 40%, 15%) 0%, 
    hsl(280, 35%, 18%) 25%, 
    hsl(260, 30%, 12%) 50%, 
    hsl(220, 25%, 14%) 75%, 
    hsl(240, 30%, 10%) 100%);
  border: 1px solid hsl(260, 15%, 25%);
  box-shadow: 0 4px 20px hsla(280, 50%, 10%, 0.4);
}

/* Success message styling */
.success-message {
  background: linear-gradient(135deg, 
    hsl(120, 60%, 8%) 0%, 
    hsl(120, 40%, 12%) 50%, 
    hsl(120, 60%, 8%) 100%);
  border: 1px solid hsl(120, 50%, 25%);
  color: hsl(120, 60%, 85%);
  box-shadow: 0 2px 10px hsla(120, 50%, 10%, 0.4);
}

/* Error message styling */
.error-message {
  background: linear-gradient(135deg, 
    hsl(0, 60%, 8%) 0%, 
    hsl(0, 40%, 12%) 50%, 
    hsl(0, 60%, 8%) 100%);
  border: 1px solid hsl(0, 50%, 25%);
  color: hsl(0, 60%, 85%);
  box-shadow: 0 2px 10px hsla(0, 50%, 10%, 0.4);
}

/* Upload preloader styling */
.upload-preloader {
  background: linear-gradient(135deg, 
    hsla(260, 40%, 15%, 0.95) 0%, 
    hsla(280, 35%, 18%, 0.95) 50%, 
    hsla(260, 40%, 15%, 0.95) 100%);
  border: 2px dashed hsl(280, 50%, 40%);
  backdrop-filter: blur(8px);
}



@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-saira antialiased;
    background: 
      radial-gradient(ellipse at 20% 30%, hsla(280, 80%, 50%, 0.4) 0%, transparent 50%),
      radial-gradient(ellipse at 80% 70%, hsla(16, 90%, 60%, 0.35) 0%, transparent 50%),
      radial-gradient(ellipse at 60% 10%, hsla(180, 80%, 50%, 0.3) 0%, transparent 50%),
      radial-gradient(ellipse at 10% 80%, hsla(220, 70%, 40%, 0.4) 0%, transparent 50%),
      hsl(260, 30%, 8%);
    background-attachment: fixed;
    min-height: 100vh;
  }

  /* Typography System - NEVER use inline fonts */
  h1 {
    @apply font-righteous;
    background: linear-gradient(135deg, hsl(16, 100%, 73%) 0%, hsl(180, 100%, 50%) 100%);
    background-size: 400% 400%;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: gradientShift 8s ease-in-out infinite;
  }

  /* All h2 headers use consistent styling */
  h2 {
    @apply font-quicksand font-light;
  }

  /* Paragraph text uses thin Roboto font */
  p {
    font-family: 'Roboto', sans-serif;
    font-weight: 100;
  }

  /* Section headers with salmon color */
  h2.salmon {
    color: #ff6b6b;
  }

  /* Section headers with cyan color */

/* Override gradient for specific gallery navigation h1 */
.gallery-nav-h1 {
  background: none !important;
  background-image: none !important;
  -webkit-background-clip: unset !important;
  -webkit-text-fill-color: white !important;
  background-clip: unset !important;
  color: white !important;
  font-weight: 100 !important;
  font-family: inherit !important;
}

/* Section headers with cyan color */
  h2.cyan {
    color: #4ecdc4;
  }

  h3 {
    @apply font-quicksand font-light text-2xl;
    color: hsl(var(--salmon));
  }

  h4 {
    @apply font-quicksand font-light text-xl;
    color: hsl(var(--cyan));
  }

  h5, h6 {
    @apply font-quicksand font-light text-lg;
    color: hsl(var(--salmon));
  }

  /* Helper classes for alternating header colors */
  .h2-salmon {
    color: hsl(16, 100%, 73%) !important;
  }

  .h2-cyan {
    color: hsl(180, 100%, 50%) !important;
  }

  .h3-salmon {
    color: hsl(var(--salmon));
  }

  .h3-cyan {
    color: hsl(var(--cyan));
  }

  .h4-salmon {
    color: hsl(var(--salmon));
  }

  /* Tagline styling */
  .tagline {
    @apply font-corinthia text-2xl;
  }

  /* Background gradient blobs for softening harsh black */
  .background-gradient-blobs {
    position: relative;
    overflow: hidden;
  }

  .background-gradient-blobs::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle at 20% 30%, rgba(255, 107, 107, 0.4) 0%, transparent 70%),
                radial-gradient(circle at 80% 70%, rgba(0, 255, 255, 0.3) 0%, transparent 70%),
                radial-gradient(circle at 50% 50%, rgba(255, 193, 7, 0.2) 0%, transparent 70%),
                radial-gradient(circle at 10% 80%, rgba(255, 107, 107, 0.25) 0%, transparent 60%),
                radial-gradient(circle at 90% 20%, rgba(0, 255, 255, 0.2) 0%, transparent 60%);
    animation: float 20s ease-in-out infinite;
    pointer-events: none;
    z-index: 0;
  }

  .background-gradient-blobs > * {
    position: relative;
    z-index: 1;
  }

  .h4-cyan {
    color: hsl(var(--cyan));
  }



  html {
    scroll-behavior: smooth;
  }

  /* Fix date picker styling */
  input[type="date"] {
    color-scheme: dark !important;
    background-color: hsl(var(--background)) !important;
    border: 1px solid hsl(var(--border)) !important;
    color: hsl(var(--foreground)) !important;
  }

  input[type="date"]::-webkit-calendar-picker-indicator {
    background-color: hsl(var(--salmon)) !important;
    border-radius: 4px !important;
    padding: 4px !important;
    cursor: pointer !important;
    filter: invert(1) !important;
  }

  input[type="date"]::-webkit-inner-spin-button,
  input[type="date"]::-webkit-outer-spin-button {
    opacity: 1 !important;
    height: 100% !important;
    position: absolute !important;
    right: 0 !important;
    top: 0 !important;
    cursor: pointer !important;
  }
}

@layer components {
  .font-saira {
    font-family: 'Saira Condensed', sans-serif;
  }

  .font-vibes {
    font-family: 'Great Vibes', cursive;
  }

  .font-forum {
    font-family: 'Forum', serif;
  }

  .font-barlow {
    font-family: 'Barlow Semi Condensed', sans-serif;
  }

  .font-righteous {
    font-family: 'Righteous', cursive;
  }

  .font-corinthia {
    font-family: 'Corinthia', cursive;
  }

  /* Card System Classes */
  .studio-card {
    @apply rounded-2xl p-8 shadow-2xl transition-all duration-500 transform hover:scale-[1.02];
    background: linear-gradient(135deg, hsl(260, 25%, 12%) 0%, hsl(220, 20%, 10%) 100%);
    border: 1px solid rgba(255, 107, 107, 0.3);
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.3), 0 0 20px rgba(255, 107, 107, 0.1);
  }

  /* Universal Button Classes - replace all inline button styling */
  .btn-salmon {
    @apply px-8 py-4 rounded-full font-barlow font-semibold text-lg transition-all duration-300;
    background: linear-gradient(135deg, hsl(16, 100%, 73%) 0%, hsl(16, 80%, 65%) 100%);
    color: white;
    border: none;
  }

  .btn-salmon:hover {
    background: linear-gradient(135deg, hsl(180, 100%, 50%) 0%, hsl(180, 80%, 45%) 100%);
    transform: translateY(-2px);
    box-shadow: 0 10px 25px rgba(78, 205, 196, 0.3);
  }

  .btn-cyan {
    @apply px-8 py-4 rounded-full font-barlow font-semibold text-lg transition-all duration-300;
    background: linear-gradient(135deg, hsl(180, 100%, 50%) 0%, hsl(180, 80%, 45%) 100%);
    color: white;
    border: none;
  }

  .btn-cyan:hover {
    background: linear-gradient(135deg, hsl(16, 100%, 73%) 0%, hsl(16, 80%, 65%) 100%);
    transform: translateY(-2px);
    box-shadow: 0 10px 25px rgba(255, 107, 107, 0.3);
  }

  .btn-outline-salmon {
    @apply px-8 py-4 rounded-full font-barlow font-semibold text-lg transition-all duration-300;
    background: transparent;
    color: hsl(16, 100%, 73%);
    border: none;
  }

  .btn-outline-salmon:hover {
    background: linear-gradient(135deg, hsl(180, 100%, 50%) 0%, hsl(180, 80%, 45%) 100%);
    color: white;
    transform: translateY(-2px);
    box-shadow: 0 10px 25px rgba(78, 205, 196, 0.3);
  }

  .btn-outline-cyan {
    @apply px-8 py-4 rounded-full font-barlow font-semibold text-lg transition-all duration-300;
    background: transparent;
    color: hsl(180, 100%, 50%);
