    return this.currentUser;
  }

  onAuthStateChange(callback: (user: SupabaseUser | null) => void) {
    // Mock implementation
    return { unsubscribe: () => {} };
  }
}

export const supabase = {
  auth: new MockSupabaseAuth()
};

