          </div>
        </div>

        {/* Mobile Menu */}
        {isMobileMenuOpen && (
          <div className="lg:hidden py-4 border-t border-border">
            <div className="space-y-4">
              <Link
                href="/photography"
                className="block py-2 text-lg font-barlow font-medium hover:text-gold transition-colors"
                onClick={() => setIsMobileMenuOpen(false)}
