client/src/components/ui/auth-button.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { LogOut, User, Settings, Shield } from "lucide-react";
import { useLocation } from "wouter";

export function AuthButton() {
  const { user, login, logout } = useAuth();
  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const [isOpen, setIsOpen] = useState(false);
  const [isSignUp, setIsSignUp] = useState(false);
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (!isSignUp) {
        await login(email, password);
        setIsOpen(false);
        setEmail("");
        setPassword("");
        toast({
          title: "Success",
          description: "Signed in successfully!"
        });
      } else {
        toast({
          title: "Sign Up",
          description: "Sign up functionality not yet implemented",
          variant: "destructive"
        });
      }
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Invalid credentials",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  const handleSignOut = () => {
    logout();
    toast({
      title: "Signed out",
      description: "You have been signed out successfully"
    });
  };

  if (user) {
    return (
      <DropdownMenu>
        <DropdownMenuTrigger asChild>
          <Button variant="ghost" className="relative h-10 w-10 rounded-full hover:bg-salmon/20">
            <Avatar className="h-10 w-10 border-2 border-salmon/40">
              <AvatarFallback className="bg-salmon text-white font-bold">
                {user.email.charAt(0).toUpperCase()}
              </AvatarFallback>
            </Avatar>
          </Button>
        </DropdownMenuTrigger>
        <DropdownMenuContent className="w-56" align="end" forceMount>
          <DropdownMenuItem className="flex items-center">
            <User className="mr-2 h-4 w-4" />
            <div className="flex flex-col space-y-1">
              <p className="text-sm font-medium">{user.email}</p>
              <p className="text-xs text-muted-foreground capitalize">{user.role}</p>
            </div>
          </DropdownMenuItem>
          <DropdownMenuItem onClick={() => setLocation("/dashboard")}>
            <Settings className="mr-2 h-4 w-4" />
            Dashboard
          </DropdownMenuItem>
          {user.role === "staff" && (
            <DropdownMenuItem onClick={() => setLocation("/admin")}>
              <Shield className="mr-2 h-4 w-4" />
              Admin Panel
            </DropdownMenuItem>
          )}
          <DropdownMenuItem onClick={handleSignOut}>
            <LogOut className="mr-2 h-4 w-4" />
            Sign out
          </DropdownMenuItem>
        </DropdownMenuContent>
      </DropdownMenu>
    );
  }

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        <Button className="bg-salmon text-white hover:bg-salmon-muted">
          Account
        </Button>
      </DialogTrigger>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle className="text-xl">
            {isSignUp ? "Create Account" : "Sign In"}
          </DialogTitle>
        </DialogHeader>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="email">Email</Label>
            <Input
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="your@email.com"
              required
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="password">Password</Label>
            <Input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Your password"
              required
            />
          </div>
          <Button 
            type="submit" 
            className={`w-full ${isSignUp ? "bg-cyan text-white hover:bg-cyan-muted" : "bg-salmon text-white hover:bg-salmon-muted"}`}
            disabled={loading}
          >
            {loading ? "Loading..." : (isSignUp ? "Create Account" : "Sign In")}
          </Button>
          <div className="text-center space-y-2">
            <button
              type="button"
              onClick={() => setShowForgotPassword(!showForgotPassword)}
              className="text-sm text-cyan hover:text-salmon transition-colors duration-300 block w-full"
            >
              Forgot your password?
            </button>
            <button
              type="button"
              onClick={() => setIsSignUp(!isSignUp)}
              className="text-sm text-cyan hover:text-salmon transition-colors duration-300"
            >
              {isSignUp ? "Already have an account? Sign in" : "Need an account? Sign up"}
            </button>
          </div>
        </form>

        {showForgotPassword && (
          <div className="mt-4 p-4 bg-cyan-dark/20 border border-cyan/30 rounded-lg">
            <h3 className="text-sm font-semibold text-salmon mb-2">Reset Password</h3>
            <p className="text-xs text-muted-foreground mb-3">
              Contact our support team to reset your password. Please include your email address and we'll help you regain access.
            </p>
            <div className="space-y-2 text-xs">
              <p><strong className="text-salmon">Email:</strong> support@slyfoxstudios.co.za</p>
              <p><strong className="text-salmon">Phone:</strong> +27 31 123 4567</p>
              <p><strong className="text-salmon">WhatsApp:</strong> +27 82 987 6543</p>
            </div>
            <Button 
              onClick={() => setShowForgotPassword(false)}
              variant="outline"
              size="sm"
              className="mt-3 w-full border-border hover:border-salmon text-white"
            >
              Got it, thanks
            </Button>
          </div>
        )}
      </DialogContent>
    </Dialog>
  );
}

