import { useAuth } from "@/hooks/use-auth";
import { Navigation } from "@/components/layout/navigation";
import { Footer } from "@/components/layout/footer";
import { ClientPortal } from "@/components/client/client-portal";
import { useLocation } from "wouter";
import { useEffect } from "react";

export default function ClientDashboard() {
  const { user, isLoading, logout } = useAuth();
  const [, setLocation] = useLocation();

  console.log('ClientDashboard: isLoading:', isLoading, 'user:', user?.email, 'role:', user?.role);

  useEffect(() => {
    console.log('ClientDashboard useEffect: isLoading:', isLoading, 'user:', user?.email);
    if (!isLoading && !user) {
      console.log('ClientDashboard: No user, redirecting to login');
      setLocation("/login");
    }
    if (!isLoading && user && user.role !== "client") {
      console.log('ClientDashboard: User is not client, redirecting to dashboard');
      setLocation("/dashboard");
    }
  }, [user, isLoading, setLocation]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-dark via-background to-grey-dark flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-salmon mx-auto mb-4"></div>
          <p className="text-lg text-muted-foreground">Loading...</p>
        </div>
      </div>
    );
  }

  if (!user || user.role !== "client") {
    return null;
  }

  // Debug logging
  console.log('ClientDashboard rendering for user:', user.email, user.role);

  return (
    <div className="min-h-screen bg-background text-foreground">
      <Navigation />
      <div className="pt-20">
        {/* Client Portal Component handles everything */}
        <ClientPortal 
          userEmail={user.email} 
          userName={user.fullName} 
        />
      </div>
      <Footer />
    </div>
  );
}

client/src/pages/login.tsx.content:
import { useState } from "react";
import { useAuth } from "@/hooks/use-auth";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { Eye, EyeOff, Camera } from "lucide-react";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const { login } = useAuth();
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      await login(email, password);

      // Get user info from localStorage to determine redirect
      const savedUser = localStorage.getItem("user");
      if (savedUser) {
        const user = JSON.parse(savedUser);
        console.log('Login redirect: user role is', user.role);
        if (user.role === "client") {
          console.log('Redirecting client to /client-portal');
          setLocation("/client-portal");
        } else if (user.role === "staff" || user.role === "super_admin") {
          console.log('Redirecting staff to /dashboard');
          setLocation("/dashboard");
        } else {
          setLocation("/");
        }
      } else {
        setLocation("/");
      }

      toast({
        title: "Login Successful",
        description: "Welcome back!"
      });
    } catch (error) {
      toast({
        title: "Login Failed",
        description: "Please check your email and password.",
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-dark via-background to-grey-dark flex items-center justify-center px-4">
      <Card className="w-full max-w-md admin-gradient-card">
        <CardHeader className="text-center">
          <div className="flex justify-center mb-4">
            <Camera className="w-12 h-12 text-salmon" />
          </div>
          <CardTitle className="text-2xl font-saira font-bold text-salmon">
            SlyFox Studios Login
          </CardTitle>
          <p className="text-muted-foreground">
            Access your galleries and manage your account
          </p>
        </CardHeader>

        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <Label htmlFor="email">Email Address</Label>
              <Input
                id="email"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="your@email.com"
                required
                disabled={isLoading}
              />
            </div>

            <div>
              <Label htmlFor="password">Password</Label>
              <div className="relative">
                <Input
                  id="password"
                  type={showPassword ? "text" : "password"}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter your password"
                  required
                  disabled={isLoading}
                  className="pr-10"
                />
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  className="absolute right-0 top-0 h-full px-3 hover:bg-transparent"
                  onClick={() => setShowPassword(!showPassword)}
                  disabled={isLoading}
                >
                  {showPassword ? (
                    <EyeOff className="w-4 h-4 text-muted-foreground" />
                  ) : (
                    <Eye className="w-4 h-4 text-muted-foreground" />
                  )}
                </Button>
              </div>
            </div>

            <Button 
              type="submit" 
              className="w-full bg-salmon text-white hover:bg-salmon-muted"
              disabled={isLoading}
            >
              {isLoading ? "Signing In..." : "Sign In"}
            </Button>
          </form>

          <div className="mt-6 text-center text-sm text-muted-foreference">
            <p>Don't have an account? Contact your photographer.</p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

