              <SelectContent>
                <SelectItem value="tight">Tight (2px gaps)</SelectItem>
                <SelectItem value="normal">Normal (8px gaps)</SelectItem>
                <SelectItem value="loose">Loose (16px gaps)</SelectItem>
              </SelectContent>
