import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Upload, Image as ImageIcon, Video, Settings, Eye, Star, AlertTriangle, Trash2 } from "lucide-react";
import { SmartImage } from "@/components/shared/smart-image";

// Asset keys defined locally to avoid circular imports
const ASSET_KEYS = {
  'hero/durban-wedding-photography-slyfox-studios': 'Main Home Page Hero',
  'hero/professional-photography-services-durban': 'Photography Services Landing',
  'hero/durban-wedding-photographer-portfolio': 'Weddings Portfolio Hero',
  'hero/portrait-photographer-durban-studio': 'Portraits Portfolio Hero',
  'hero/corporate-photography-durban-business': 'Corporate Photography Hero',
  'hero/event-photographer-durban-professional': 'Events Portfolio Hero',
  'hero/graduation-photography-durban-ceremony': 'Graduation Photography Hero',
  'hero/product-photography-durban-commercial': 'Product Photography Hero',
  'hero/matric-dance-photographer-durban': 'Matric Dance Photography Hero',
  'backgrounds/photography-studio-durban-texture': 'Main Site Background',
  'backgrounds/wedding-photography-background-elegant': 'Wedding Portfolio Background',
  'backgrounds/portrait-photography-studio-backdrop': 'Portrait Studio Background'
} as const;
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { LocalSiteAsset, ImageClassification, IMAGE_CLASSIFICATIONS } from "@shared/schema";

interface SiteAssetsPanelProps {
  userRole: string;
}

export const SiteAssetsPanel: React.FC<SiteAssetsPanelProps> = ({ userRole }) => {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [uploadingAsset, setUploadingAsset] = useState<string | null>(null);
  const [editingAltText, setEditingAltText] = useState<string | null>(null);

  // Only super_admin can access this panel
  if (userRole !== 'super_admin') {
    return (
      <Card>
        <CardContent className="text-center py-8">
          <AlertTriangle className="w-12 h-12 mx-auto text-amber-500 mb-4" />
          <h3 className="text-lg font-semibold mb-2">Super Admin Access Required</h3>
          <p className="text-gray-600">Site asset management is restricted to super administrators.</p>
        </CardContent>
      </Card>
    );
  }

  // Fetch local site assets
  const { data: localAssets, isLoading } = useQuery({
    queryKey: ['local-site-assets'],
    queryFn: async () => {
      try {
        const response = await apiRequest('GET', '/api/local-assets');
        return Array.isArray(response) ? response as LocalSiteAsset[] : [] as LocalSiteAsset[];
      } catch (error) {
        console.warn('Failed to fetch local assets:', error);
        return [] as LocalSiteAsset[];
      }
    }
  });

  // Fetch featured images for homepage gallery management
  const { data: featuredImages } = useQuery({
    queryKey: ['featured-images'],
    queryFn: async () => {
      try {
        const response = await apiRequest('GET', '/api/images/featured');
        return response as any[];
      } catch (error) {
        console.warn('Failed to fetch featured images:', error);
        return [] as any[];
      }
    }
  });

  // Upload new asset mutation
  const uploadAsset = useMutation({
    mutationFn: async ({ assetKey, file }: { assetKey: string, file: File }) => {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('assetKey', assetKey);

      const response = await fetch('/api/local-assets/upload', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      return response.json();
    },
    onSuccess: (data, variables) => {
      // Force a complete cache refresh to ensure UI updates
      queryClient.removeQueries({ queryKey: ['local-site-assets'] });
      queryClient.invalidateQueries({ queryKey: ['local-site-assets'] });
      toast({
        title: "Success",
        description: `Asset ${variables.assetKey} updated successfully`
      });
      setUploadingAsset(null);
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: 'Failed to upload asset',
        variant: "destructive"
      });
      setUploadingAsset(null);
    }
  });

  // Update alt text mutation
  const updateAltText = useMutation({
    mutationFn: async ({ assetKey, altText }: { assetKey: string, altText: string }) => {
      const response = await fetch(`/api/local-assets/${assetKey}/alt-text`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ altText })
      });

      if (!response.ok) {
        throw new Error('Update failed');
      }

      return response.json();
    },
    onSuccess: () => {
      // Force complete cache refresh for alt text updates
      queryClient.removeQueries({ queryKey: ['local-site-assets'] });
      queryClient.invalidateQueries({ queryKey: ['local-site-assets'] });
      toast({
        title: "Success",
        description: 'Alt text updated successfully'
      });
      setEditingAltText(null);
    }
  });

  // Remove asset mutation (reverts to fallback)
  const removeAsset = useMutation({
    mutationFn: async (assetKey: string) => {
      const response = await fetch(`/api/local-assets/${assetKey}`, {
        method: 'DELETE'
      });

      if (!response.ok) {
        throw new Error('Remove failed');
      }

      return response.json();
    },
    onSuccess: (data, assetKey) => {
      // Force complete cache refresh for removals
      queryClient.removeQueries({ queryKey: ['local-site-assets'] });
      queryClient.invalidateQueries({ queryKey: ['local-site-assets'] });
      toast({
        title: "Success",
        description: `Asset removed - reverted to fallback image`
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: 'Failed to remove asset',
        variant: "destructive"
      });
    }
  });

  // Validate alt text input
  const validateAltText = (text: string): string => {
    return text
      .trim()
      .toLowerCase()
      .replace(/[^a-z0-9\s-]/g, '') // Remove special characters
      .replace(/\s+/g, '-')         // Replace spaces with hyphens
      .replace(/-+/g, '-')          // Remove multiple consecutive hyphens
      .slice(0, 125);               // SEO optimal length
  };

  const handleFileUpload = (assetKey: string, event: React.ChangeEvent<HTMLInputElement> | { target: { files: File[] } }) => {
    const file = event.target.files?.[0];
    if (!file) return;

    // Validate file type
    const validTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/webp'];
    if (!validTypes.includes(file.type)) {
      toast({
        title: "Error",
        description: 'Please upload a valid image file (JPG, PNG, or WebP)',
        variant: "destructive"
      });
      return;
    }

    // Validate file size (max 10MB)
    if (file.size > 10 * 1024 * 1024) {
      toast({
        title: "Error",
        description: 'File size must be less than 10MB',
        variant: "destructive"
      });
      return;
    }

    setUploadingAsset(assetKey);
    uploadAsset.mutate({ assetKey, file });
  };

  const AssetCard = ({ assetKey, asset }: { assetKey: string, asset?: LocalSiteAsset }) => {
    const [isDragOver, setIsDragOver] = useState(false);

    const handleDragOver = (e: React.DragEvent) => {
      e.preventDefault();
      setIsDragOver(true);
    };

    const handleDragLeave = (e: React.DragEvent) => {
      e.preventDefault();
      setIsDragOver(false);
    };

    const handleDrop = (e: React.DragEvent) => {
      e.preventDefault();
      setIsDragOver(false);

      const files = e.dataTransfer.files;
      if (files.length > 0) {
        const file = files[0];
        if (file.type.startsWith('image/')) {
          handleFileUpload(assetKey, { target: { files: [file] } } as any);
        } else {
          toast({
            title: "Invalid file type",
            description: "Please drop an image file (JPG, PNG, WebP)",
            variant: "destructive"
          });
        }
      }
    };

    return (
      <Card className="overflow-hidden bg-gradient-to-br from-purple-900/20 via-indigo-900/15 to-purple-800/25 border border-purple-500/30">
        <CardContent className="p-4">
          <div 
            className={`aspect-video mb-4 rounded-lg overflow-hidden relative border-2 border-dashed transition-colors ${
              isDragOver ? 'border-salmon bg-salmon/10' : 'border-purple-400/40'
            }`}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
          >
            <SmartImage
              assetKey={assetKey}
              alt={asset?.altText || `${assetKey} image`}
              className="w-full h-full object-cover border-2 border-purple-400/40 rounded"
              onFallbackUsed={(key) => console.log(`Fallback used for ${key}`)}
            />
            {isDragOver && (
              <div className="absolute inset-0 bg-salmon/20 flex items-center justify-center">
                <div className="text-salmon font-medium">Drop image here</div>
              </div>
            )}
          </div>

        <div className="space-y-3">
          <div>
            <h4 className="font-semibold text-sm text-white">{ASSET_KEYS[assetKey as keyof typeof ASSET_KEYS] || assetKey}</h4>
            <p className="text-xs text-purple-200">
              {asset?.altText || 'No alt text set'}
            </p>
          </div>

          <div className="flex gap-2">
            {/* Upload new image */}
            <label className="flex-1">
              <Button 
                size="sm" 
                className="w-full bg-gradient-to-r from-purple-600 to-indigo-600 hover:from-purple-700 hover:to-indigo-700 border border-purple-400/30"
                disabled={uploadingAsset === assetKey}
              >
                <Upload className="w-3 h-3 mr-1" />
                {uploadingAsset === assetKey ? 'Uploading...' : 'Upload Image'}
              </Button>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => handleFileUpload(assetKey, e)}
                className="hidden"
              />
            </label>

            {/* Remove Image Button - Always show but conditional functionality */}
            <Button
              size="sm"
              variant="outline"
              className="flex-shrink-0 border-red-400/30 hover:bg-red-500/20 hover:border-red-400"
              onClick={() => removeAsset.mutate(assetKey)}
              disabled={removeAsset.isPending || !asset}
              title={asset ? "Remove image and revert to fallback" : "No custom image to remove"}
            >
              <Trash2 className="w-3 h-3" />
            </Button>

            {/* Edit alt text */}
            <Dialog>
              <DialogTrigger asChild>
                <Button size="sm" variant="outline" className="border-purple-400/30 hover:bg-purple-500/20">
                  <Settings className="w-3 h-3" />
                  Alt Text
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Edit Alt Text - {assetKey}</DialogTitle>
                </DialogHeader>
                <div className="space-y-4">
                  <div>
                    <Label htmlFor="altText">Alt Text (SEO Description)</Label>
                    <Input
                      id="altText"
                      defaultValue={asset?.altText || ''}
                      placeholder="Professional Cape Town wedding photographer"
                      maxLength={255}
                      onChange={(e) => {
                        const preview = validateAltText(e.target.value);
                        // Show preview in real-time
                      }}
                    />
                    <p className="text-xs text-gray-500 mt-1">
                      Only letters, numbers, and hyphens allowed. Spaces will be converted to hyphens.
                    </p>
                  </div>
                  <Button 
                    onClick={() => {
                      const input = document.getElementById('altText') as HTMLInputElement;
                      const validatedText = validateAltText(input.value);
                      updateAltText.mutate({ assetKey, altText: validatedText });
                    }}
                    disabled={updateAltText.isPending}
                    className="w-full"
                  >
                    {updateAltText.isPending ? 'Updating Alt Text...' : 'Save Alt Text'}
                  </Button>
                </div>
              </DialogContent>
            </Dialog>
          </div>
        </div>
      </CardContent>
    </Card> 
    );
  };

  if (isLoading) {
    return (
      <Card>
        <CardContent className="text-center py-8">
          <div className="w-8 h-8 border-2 border-salmon border-t-transparent rounded-full animate-spin mx-auto mb-4" />
          <p>Loading site assets...</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <Card className="bg-gradient-to-br from-purple-900/30 via-indigo-900/20 to-purple-800/35 border border-purple-500/40">
        <CardHeader>
          <CardTitle className="flex items-center gap-2 text-white">
            <Settings className="w-5 h-5" />
            Site Asset Management
          </CardTitle>
          <p className="text-sm text-purple-200">
            Manage hero images, backgrounds, and featured content. Each asset has a fallback system for reliability.
          </p>
        </CardHeader>
      </Card>

      <Tabs defaultValue="local-assets" className="space-y-6">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="local-assets" className="flex items-center gap-2">
            <ImageIcon className="w-4 h-4" />
            Local Assets
          </TabsTrigger>
          <TabsTrigger value="featured-work" className="flex items-center gap-2">
            <Star className="w-4 h-4" />
            Featured Work
          </TabsTrigger>
        </TabsList>

        <TabsContent value="local-assets" className="space-y-6">
          {/* Hero Images Section */}
          <Card className="bg-gradient-to-br from-purple-900/25 via-indigo-900/15 to-purple-800/30 border border-purple-500/30">
            <CardHeader>
              <CardTitle className="text-lg text-white">Hero Images (9 Total)</CardTitle>
              <p className="text-sm text-purple-200">
                Primary images displayed on key pages across the photography website. Each has automatic fallback protection.
              </p>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {Object.entries(ASSET_KEYS)
                  .filter(([key]) => key.startsWith('hero/'))
                  .map(([assetKey, title]) => (
                    <AssetCard 
                      key={assetKey}
                      assetKey={assetKey} 
                      asset={Array.isArray(localAssets) ? localAssets.find((a) => a.assetKey === assetKey) : undefined}
                    />
                  ))}
              </div>
            </CardContent>
          </Card>

          {/* Background Images Section */}
          <Card className="bg-gradient-to-br from-purple-900/25 via-indigo-900/15 to-purple-800/30 border border-purple-500/30">
            <CardHeader>
              <CardTitle className="text-lg text-white">Background Images (3 Total)</CardTitle>
              <p className="text-sm text-purple-200">
                Subtle background textures and patterns used throughout the site sections.
              </p>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {Object.entries(ASSET_KEYS)
                  .filter(([key]) => key.startsWith('backgrounds/'))
                  .map(([assetKey, title]) => (
                    <AssetCard 
                      key={assetKey}
                      assetKey={assetKey} 
                      asset={Array.isArray(localAssets) ? localAssets.find((a) => a.assetKey === assetKey) : undefined}
                    />
                  ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="featured-work" className="space-y-6">
          <Card className="bg-gradient-to-br from-purple-900/25 via-indigo-900/15 to-purple-800/30 border border-purple-500/30">
            <CardHeader>
              <CardTitle className="text-lg text-white">Featured Work Gallery</CardTitle>
              <p className="text-sm text-purple-200">
                Images displayed on the homepage. Currently showing {featuredImages?.length || 0} featured images.
              </p>
            </CardHeader>
            <CardContent>
              {featuredImages && featuredImages.length > 0 ? (
                <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
                  {featuredImages.map((image: any) => (
                    <div key={image.id} className="relative group">
                      <img 
                        src={`https://your-supabase-url.supabase.co/storage/v1/object/public/images/${image.storagePath}`}
                        alt={`Featured ${image.classification} photography`}
                        className="aspect-square object-cover rounded"
                      />
                      <Badge 
                        className="absolute bottom-2 left-2 bg-black/75 text-white text-xs"
                      >
                        {image.classification}
                      </Badge>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8 text-gray-500">
                  <Star className="w-12 h-12 mx-auto mb-4 opacity-50" />
                  <p>No featured images selected yet.</p>
                  <p className="text-sm">Use the Gallery Management tab to add featured images.</p>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
};

client/src/components/admin/staff-manage.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger, DialogDescription } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import {
  Users,
  Plus,
  Search,
  Edit,
  Trash2,
  Mail,
  Shield,
  Crown,
  User
} from "lucide-react";

interface Profile {
  id: string;
  email: string;
  fullName?: string;
  role: "super_admin" | "staff" | "client";
  profileImageUrl?: string;
  bannerImageUrl?: string;
  themePreference: string;
  createdAt: string;
  updatedAt: string;
}

interface CreateStaffData {
  email: string;
  fullName: string;
  role: "staff" | "super_admin";
  password: string;
}

interface UpdateStaffData {
  fullName?: string;
  role?: "staff" | "super_admin";
  themePreference?: string;
}

export function StaffManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [searchTerm, setSearchTerm] = useState("");
  const [newStaffOpen, setNewStaffOpen] = useState(false);
  const [editingStaff, setEditingStaff] = useState<Profile | null>(null);
  const [formData, setFormData] = useState({
    email: "",
    fullName: "",
    role: "staff" as "staff" | "super_admin",
    password: ""
  });

  // Fetch all staff members (super_admin and staff roles only)
  const { data: staffMembers, isLoading: staffLoading } = useQuery<Profile[]>({
    queryKey: ["/api/staff"],
  });

  // Create staff mutation
  const createStaffMutation = useMutation({
    mutationFn: (data: CreateStaffData) => apiRequest("POST", "/api/staff", data),
    onSuccess: () => {
      toast({
        title: "Staff Member Added",
        description: "New staff member has been successfully created."
      });
      setNewStaffOpen(false);
      setFormData({ email: "", fullName: "", role: "staff", password: "" });
      queryClient.invalidateQueries({ queryKey: ["/api/staff"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create staff member.",
        variant: "destructive"
      });
    }
  });

  // Update staff mutation
  const updateStaffMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: UpdateStaffData }) => 
      apiRequest("PATCH", `/api/staff/${id}`, data),
    onSuccess: () => {
      toast({
        title: "Staff Member Updated",
        description: "Staff member details have been successfully updated."
      });
      setEditingStaff(null);
      queryClient.invalidateQueries({ queryKey: ["/api/staff"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update staff member.",
        variant: "destructive"
      });
    }
  });

  // Delete staff mutation
  const deleteStaffMutation = useMutation({
    mutationFn: (id: string) => apiRequest("DELETE", `/api/staff/${id}`),
    onSuccess: () => {
      toast({
        title: "Staff Member Removed",
        description: "Staff member has been successfully removed."
      });
      queryClient.invalidateQueries({ queryKey: ["/api/staff"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to remove staff member.",
        variant: "destructive"
      });
    }
  });

  const handleCreateStaff = (e: React.FormEvent) => {
    e.preventDefault();
    createStaffMutation.mutate(formData);
  };

  const handleUpdateStaff = (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingStaff) return;

    updateStaffMutation.mutate({
      id: editingStaff.id,
      data: {
        fullName: formData.fullName,
        role: formData.role,
      }
    });
  };

  const handleDeleteStaff = (staff: Profile) => {
    if (staff.role === "super_admin") {
      toast({
        title: "Cannot Delete Super Admin",
        description: "Super admin accounts cannot be deleted for security reasons.",
        variant: "destructive"
      });
      return;
    }

    if (confirm(`Are you sure you want to remove ${staff.fullName || staff.email}? This action cannot be undone.`)) {
      deleteStaffMutation.mutate(staff.id);
    }
  };

  const filteredStaffMembers = staffMembers?.filter(staff =>
    (staff.fullName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
     staff.email.toLowerCase().includes(searchTerm.toLowerCase()))
  ) || [];

  const getRoleIcon = (role: string) => {
    switch (role) {
      case "super_admin":
        return <Crown className="w-4 h-4 text-salmon" />;
      case "staff":
        return <Shield className="w-4 h-4 text-cyan" />;
      default:
        return <User className="w-4 h-4 text-muted-foreground" />;
    }
  };

  const getRoleBadge = (role: string) => {
    switch (role) {
      case "super_admin":
        return <Badge className="bg-salmon text-white">Super Admin</Badge>;
      case "staff":
        return <Badge className="bg-cyan text-black">Staff</Badge>;
      default:
        return <Badge variant="outline">Unknown</Badge>;
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-saira font-bold text-salmon">Staff Management</h2>
        <div className="flex gap-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
            <Input
              placeholder="Search staff..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10"
            />
          </div>
          <Dialog open={newStaffOpen} onOpenChange={setNewStaffOpen}>
            <DialogTrigger asChild>
              <Button className="bg-salmon text-white hover:bg-salmon-muted">
                <Plus className="w-4 h-4 mr-2" />
                Add Staff Member
              </Button>
            </DialogTrigger>
            <DialogContent className="bg-cyan-dark border border-cyan/30 shadow-lg max-w-md">
              <DialogHeader>
                <DialogTitle className="text-salmon">Add New Staff Member</DialogTitle>
                <DialogDescription className="text-muted-foreground">
                  Create a new staff account with appropriate permissions.
                </DialogDescription>
              </DialogHeader>
              <form onSubmit={handleCreateStaff} className="space-y-4">
                <div>
                  <Label htmlFor="email">Email Address</Label>
                  <Input
                    id="email"
                    type="email"
                    value={formData.email}
                    onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                    placeholder="staff@slyfox.co.za"
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="fullName">Full Name</Label>
                  <Input
                    id="fullName"
                    value={formData.fullName}
                    onChange={(e) => setFormData({ ...formData, fullName: e.target.value })}
                    placeholder="John Smith"
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="role">Role</Label>
                  <Select value={formData.role} onValueChange={(value: "staff" | "super_admin") => 
                    setFormData({ ...formData, role: value })}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="staff">Staff</SelectItem>
                      <SelectItem value="super_admin">Super Admin</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label htmlFor="password">Temporary Password</Label>
                  <Input
                    id="password"
                    type="password"
                    value={formData.password}
                    onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                    placeholder="Enter temporary password"
                    required
                  />
                </div>
                <Button 
                  type="submit" 
                  className="w-full bg-salmon text-white hover:bg-salmon-muted"
                  disabled={createStaffMutation.isPending}
                >
                  {createStaffMutation.isPending ? "Creating..." : "Add Staff Member"}
                </Button>
              </form>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Staff Members Grid */}
      <div className="grid gap-4">
        {staffLoading ? (
          <div className="text-center py-8">Loading staff members...</div>
        ) : filteredStaffMembers.length === 0 ? (
          <div className="text-center py-8 text-muted-foreground">
            {searchTerm ? 'No staff members found matching your search.' : 'No staff members found.'}
          </div>
        ) : (
          filteredStaffMembers.map(staff => (
            <Card key={staff.id} className="admin-gradient-card">
              <CardContent className="p-6">
                <div className="flex justify-between items-start">
                  <div className="space-y-2">
                    <div className="flex items-center gap-3">
                      {getRoleIcon(staff.role)}
                      <h3 className="text-lg font-semibold text-salmon">
                        {staff.fullName || staff.email}
                      </h3>
                      {getRoleBadge(staff.role)}
                    </div>

                    <div className="space-y-1 text-sm text-muted-foreground">
                      <div className="flex items-center gap-2">
                        <Mail className="w-4 h-4 icon-cyan" />
                        {staff.email}
                      </div>
                      <div className="text-xs text-muted-foreground">
                        Created: {new Date(staff.createdAt).toLocaleDateString()}
                      </div>
                    </div>
                  </div>

                  <div className="flex gap-2">
                    <Button 
                      size="sm" 
                      variant="outline" 
                      className="border-border hover:border-salmon text-white"
                      onClick={() => {
                        setEditingStaff(staff);
                        setFormData({
                          email: staff.email,
                          fullName: staff.fullName || "",
                          role: staff.role as "staff" | "super_admin",
                          password: ""
                        });
                      }}
                    >
                      <Edit className="w-4 h-4" />
                    </Button>
                    {staff.role !== "super_admin" && (
                      <Button 
                        size="sm" 
                        variant="outline" 
                        className="border-border hover:border-red-500 text-white"
                        onClick={() => handleDeleteStaff(staff)}
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>

      {/* Edit Staff Dialog */}
      <Dialog open={!!editingStaff} onOpenChange={() => setEditingStaff(null)}>
        <DialogContent className="bg-cyan-dark border border-cyan/30 shadow-lg max-w-md">
          <DialogHeader>
            <DialogTitle className="text-salmon">Edit Staff Member</DialogTitle>
            <DialogDescription className="text-muted-foreground">
              Update staff member details and permissions.
            </DialogDescription>
          </DialogHeader>
          <form onSubmit={handleUpdateStaff} className="space-y-4">
            <div>
              <Label htmlFor="editEmail">Email Address</Label>
              <Input
                id="editEmail"
                type="email"
                value={formData.email}
                disabled
                className="bg-muted"
              />
            </div>
            <div>
              <Label htmlFor="editFullName">Full Name</Label>
              <Input
                id="editFullName"
                value={formData.fullName}
                onChange={(e) => setFormData({ ...formData, fullName: e.target.value })}
                placeholder="John Smith"
                required
              />
            </div>
            <div>
              <Label htmlFor="editRole">Role</Label>
              <Select value={formData.role} onValueChange={(value: "staff" | "super_admin") => 
                setFormData({ ...formData, role: value })}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="staff">Staff</SelectItem>
                  <SelectItem value="super_admin">Super Admin</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <Button 
              type="submit" 
              className="w-full bg-salmon text-white hover:bg-salmon-muted"
              disabled={updateStaffMutation.isPending}
            >
              {updateStaffMutation.isPending ? "Updating..." : "Update Staff Member"}
            </Button>
          </form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/components/client/client-port.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { ImageUrl } from "@/lib/image-utils";
import { useAuth } from "@/hooks/use-auth";
import { apiRequest } from "@/lib/queryClient";
import { GalleryLivePreview } from "@/components/shared/gallery-live-preview";
import { 
  Camera, 
  Calendar, 
  MapPin, 
  Eye, 
  Download, 
  Search,
  Heart,
  Share2,
  Filter,
  Grid3X3,
  List,
  LogOut,
  User,
  Settings,
  Palette,
  Save,
  Crown,
  X,
  Trash2,
  MousePointer,
  ChevronUp,
  ChevronDown,
  ExternalLink
} from "lucide-react";

interface Shoot {
  id: string;
  clientId: string;
  title: string;
  location: string;
  shootDate: string;
  shootType: string;
  description: string;
  notes: string;
  isPrivate: boolean;
  bannerImageId: string | null;
  seoTags: string;
  viewCount: number;
  createdBy: string;
  customSlug: string;
  customTitle: string;
  gallerySettings: {
    padding: string;
    borderStyle: string;
    layoutStyle: string;
    imageSpacing: string;
    backgroundColor: string;
  };
  createdAt: string;
  updatedAt: string;
}

interface Image {
  id: string;
  shootId: string;
  filename: string;
  storagePath: string;
  originalName: string;
  sequence: number;
  downloadCount: number;
  createdAt: string;
}

interface ClientPortalProps {
  userEmail: string;
  userName?: string;
}

export function ClientPortal({ userEmail, userName }: ClientPortalProps) {
  const { logout } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedShoot, setSelectedShoot] = useState<string | null>(null);
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [filterType, setFilterType] = useState<string>('all');
  const [activeTab, setActiveTab] = useState<'images' | 'shootinfo' | 'settings' | 'preview'>('images');
  const [expandedCards, setExpandedCards] = useState<Record<string, boolean>>({
    shootInfo: false,
    gallerySettings: false,
    livePreview: false
  });
  const [gallerySettings, setGallerySettings] = useState({
    backgroundColor: "#1a1a1a",
    layoutStyle: "masonry",
    borderStyle: "rounded", 
    imageSpacing: "normal"
  });
  const [selectedCover, setSelectedCover] = useState<string | null>(null);
  const [visibleImageCount, setVisibleImageCount] = useState(20);
  const [draggedImage, setDraggedImage] = useState<string | null>(null);
  const [dragStartTime, setDragStartTime] = useState<number>(0);
  const [imageOrder, setImageOrder] = useState<string[]>([]);

  // Debug logging to help identify loading issues
  console.log('ClientPortal loading for:', userEmail);

  // Fetch client's shoots based on email
  const { data: shoots = [], isLoading: shootsLoading, error } = useQuery<Shoot[]>({
    queryKey: ["/api/client/shoots", userEmail],
    queryFn: () => fetch(`/api/client/shoots?email=${encodeURIComponent(userEmail)}`).then(res => {
      console.log('API Response status:', res.status);
      return res.json();
    }),
  });

  // Debug logging
  console.log('Shoots loading:', shootsLoading, 'Error:', error, 'Shoots count:', shoots.length);
  console.log('Shoots data:', shoots);

  // Fetch shoot data with images (same as admin approach)
  const { data: shootData, isLoading: imagesLoading } = useQuery({
    queryKey: ["/api/shoots", selectedShoot],
    queryFn: () => fetch(`/api/shoots/${selectedShoot}`).then(res => res.json()),
    enabled: !!selectedShoot,
  });

  const shoot = (shootData as any)?.shoot || null;
  const images: Image[] = (shootData as any)?.images ? ((shootData as any).images as Image[]) : [];

  // Initialize all settings from shoot data when shoot changes (same as admin)
  useEffect(() => {
    if (shoot && shoot.id && images.length > 0) {
      // Set cover: use bannerImageId if valid, otherwise use first image as fallback
      if (shoot.bannerImageId && images.some(img => img.id === shoot.bannerImageId)) {
        setSelectedCover(shoot.bannerImageId);
      } else {
        setSelectedCover(images[0].id);
      }

      // Initialize gallery settings from shoot data (provide defaults for null gallerySettings)
      const settings = shoot.gallerySettings || {};
      setGallerySettings({
        backgroundColor: settings.backgroundColor || '#1a1a1a',
        borderStyle: settings.borderStyle || 'rounded',
        layoutStyle: settings.layoutStyle || 'masonry',
        imageSpacing: settings.imageSpacing || 'normal'
      });
    } else {
      // Clear state when switching galleries to prevent stale state
      setSelectedCover(null);
    }
  }, [shoot?.id, images.length]);

  // Initialize image order from sequence - fix blank gaps issue
  useEffect(() => {
    if (images.length > 0) {
      const sortedImages = [...images].sort((a, b) => a.sequence - b.sequence);
      const newOrder = sortedImages.map(img => img.id);

      // Only update if the order actually changed to prevent unnecessary re-renders
      if (JSON.stringify(newOrder) !== JSON.stringify(imageOrder)) {
        setImageOrder(newOrder);
      }
    } else {
      // Clear order when no images
      if (imageOrder.length > 0) {
        setImageOrder([]);
      }
    }
  }, [images.length, images.map(img => `${img.id}-${img.sequence}`).join(',')]);

  // Get ordered images for display
  const getOrderedImages = () => {
    if (imageOrder.length === 0) return images;

    const imageMap = new Map(images.map(img => [img.id, img]));
    const orderedImages = imageOrder
      .map(id => imageMap.get(id))
      .filter(Boolean) as Image[];

    // Add any new images not in order yet
    const orderedIds = new Set(imageOrder);
    const newImages = images.filter(img => !orderedIds.has(img.id));

    return [...orderedImages, ...newImages];
  };

  const filteredShoots = shoots.filter(shoot => {
    const matchesSearch = shoot.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         shoot.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         shoot.location.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesFilter = filterType === 'all' || shoot.shootType === filterType;

    return matchesSearch && matchesFilter;
  });

  const uniqueShootTypes = Array.from(new Set(shoots.map(shoot => shoot.shootType)));

  const handleDownloadImage = async (image: Image) => {
    try {
      // Use full resolution URL for downloads
      const response = await fetch(ImageUrl.forFullSize(image.storagePath));
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = image.originalName || image.filename;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      console.error('Download failed:', error);
    }
  };

  const handleViewFullRes = (image: Image) => {
    // Open full resolution image in new tab
    window.open(ImageUrl.forFullSize(image.storagePath), '_blank');
  };

  // Client-specific save order handler
  const handleSaveOrder = async () => {
    if (!selectedShoot) return;

    try {
      const imageSequences = imageOrder.length > 0 
        ? Object.fromEntries(imageOrder.map((id, index) => [id, index + 1]))
        : {};

      await apiRequest('PATCH', `/api/shoots/${selectedShoot}`, {
        bannerImageId: selectedCover,
        imageSequences: imageSequences
      });

      queryClient.invalidateQueries({ queryKey: ['/api/client/shoots'] });
      queryClient.invalidateQueries({ queryKey: ['/api/shoots', selectedShoot] });
      toast({ title: "Image order and cover saved successfully!" });
    } catch (error) {
      toast({ title: "Error", description: "Failed to save image order", variant: "destructive" });
    }
  };

  const formatDate = (dateString: string) => {
    try {
      return new Date(dateString).toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
    } catch {
      return dateString;
    }
  };

  const getShootTypeColor = (type: string) => {
    const colors = {
      'wedding': 'bg-pink-500',
      'portrait': 'bg-blue-500',
      'commercial': 'bg-green-500',
      'event': 'bg-purple-500',
      'lifestyle': 'bg-cyan-500',
      'other': 'bg-gray-500'
    };
    return colors[type as keyof typeof colors] || 'bg-gray-500';
  };

  if (shootsLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-dark via-background to-grey-dark flex items-center justify-center">
        <div className="text-center">
          <Camera className="w-12 h-12 text-salmon mx-auto mb-4 animate-pulse" />
          <p className="text-lg text-muted-foreground">Loading your galleries...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-dark via-background to-grey-dark">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">

        {/* Header with User Menu */}
        <div className="mb-8">
          <div className="flex justify-between items-start mb-4">
            <div>
              <h1 className="text-3xl font-saira font-bold text-salmon mb-2">
                Welcome back{userName ? `, ${userName}` : ''}
              </h1>
              <p className="text-muted-foreground">
                Access your private galleries and download your photos
              </p>
            </div>

            {/* User Menu */}
            <div className="flex items-center gap-2">
              <div className="flex items-center gap-2 px-3 py-2 bg-background/50 rounded-lg border border-border">
                <User className="w-4 h-4 text-cyan" />
                <span className="text-sm">{userEmail}</span>
              </div>
              <Button 
                variant="outline" 
                size="sm" 
                onClick={logout}
                className="border-border hover:border-salmon"
              >
                <LogOut className="w-4 h-4 mr-2" />
                Logout
              </Button>
            </div>
          </div>

          {/* Quick Stats */}
          <div className="flex gap-4 text-sm">
            <div className="flex items-center gap-2">
              <Camera className="w-4 h-4 text-cyan" />
              <span>{shoots.length} galleries available</span>
            </div>
            <div className="flex items-center gap-2">
              <Eye className="w-4 h-4 text-salmon" />
              <span>{shoots.reduce((total, shoot) => total + shoot.viewCount, 0)} total views</span>
            </div>
          </div>
        </div>

        {/* Gallery Selection */}
        {!selectedShoot && (
          <>
            {/* Search and Filters */}
            <div className="flex flex-col md:flex-row gap-4 mb-6">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
                <Input
                  placeholder="Search your galleries..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>

              <div className="flex gap-2">
                <select
                  value={filterType}
                  onChange={(e) => setFilterType(e.target.value)}
                  className="px-3 py-2 bg-background border border-border rounded-md text-sm"
                >
                  <option value="all">All Types</option>
                  {uniqueShootTypes.map(type => (
                    <option key={type} value={type}>
                      {type.charAt(0).toUpperCase() + type.slice(1)}
                    </option>
                  ))}
                </select>

                <Button
                  variant="outline"
                  size="sm"
